import React, { Component } from 'react';
import { connect } from 'react-redux'
import { fetchCart } from "./actions/cart";
import axios from "axios";
import { authAxios } from "../utils";

import { addToCartURL, productDetailURL } from "../constants";
import {Button,Icon} from "semantic-ui-react";

class Home extends Component{
    state = {
        loading: false,
        error: null,
        formVisible: false,
        data: [],
        formData: {}
      };

      componentDidMount() {
        this.handleFetchItem();
      }

      handleToggleForm = () => {
        const { formVisible } = this.state;
        this.setState({
          formVisible: !formVisible
        });
      };

      handleFetchItem = () => {
        const {
          match: { params }
        } = this.props;
        this.setState({ loading: true });
        axios
          .get(productDetailURL(params.productID))
          .then(res => {
            this.setState({ data: res.data, loading: false });
          })
          .catch(err => {
            this.setState({ error: err, loading: false });
          });
      };

      handleFormatData = formData => {
        // convert {colour: 1, size: 2} to [1,2] - they're all variations
        return Object.keys(formData).map(key => {
          return formData[key];
        });
      };

      handleAddToCart = slug => {
        this.setState({ loading: true });
        const { formData } = this.state;
        const variations = this.handleFormatData(formData);
        authAxios
          .post(addToCartURL, { slug, variations })
          .then(res => {
            this.props.refreshCart();
            this.setState({ loading: false });
          })
          .catch(err => {
            this.setState({ error: err, loading: false });
          });
      };

      handleChange = (e, { name, value }) => {
        const { formData } = this.state;
        const updatedFormData = {
          ...formData,
          [name]: value
        };
        this.setState({ formData: updatedFormData });
      };


    render(){
        const { data, error, formData, formVisible, loading } = this.state;
        let itemList = data.map(item => {
            return(

                <div className="card" key={item.id}>
                        <div className="card-image">
                            <img src={item.image} alt={item.title}/>

                            <span to="/" className="btn-floating halfway-fab waves-effect waves-light red" 
                            onClick={()=>{this.handleAddToCart(item.id)}}><i className="material-icons">add</i></span>
                        </div>

                        <div className="card-content">

                            <p>{item.description}</p>
                            <p><b>Price: {item.price}$</b></p>
                        </div>
                        <React.Fragment>
                    <Button
                      fluid
                      color="yellow"
                      floated="right"
                      icon
                      labelPosition="right"
                      onClick={this.handleToggleForm}
                    >
                      Add to cart
                      <Icon name="cart plus" />
                    </Button>
                  </React.Fragment>
                 </div>

            )
        })

        return(
            <div className="container">
                <h3 className="center">Our items</h3>
                <div className="box">
                    {itemList}
                </div>
            </div>
        )
    }
}
const mapStateToProps = (state)=>{
    return {
      items: state.data
    }
  }

const mapDispatchToProps = dispatch => {
    return {
      refreshCart: () => dispatch(fetchCart())
    };
  };

export default connect(mapStateToProps,mapDispatchToProps)(Home)
