import React,{ Component } from 'react';
import { Grid, Item} from "semantic-ui-react";

class ProductDetail extends Component {
render(){
  return(

    <Grid columns={2} divided>
    <Grid.Row>
      <Grid.Column>
        <Card
          fluid
          image={item.image}
          header={item.title}
          meta={
            <React.Fragment>
              {item.category}
              {item.discount_price && (
                <Label
                  color={
                    item.label === "primary"
                      ? "blue"
                      : item.label === "secondary"
                      ? "green"
                      : "olive"
                  }
                >
                  {item.label}
                </Label>
              )}
            </React.Fragment>
          }
          description={item.description}
          extra={
            <React.Fragment>
              <Button
                fluid
                color="yellow"
                floated="right"
                icon
                labelPosition="right"
                onClick={this.handleToggleForm}
              >
                Add to cart
                <Icon name="cart plus" />
              </Button>
            </React.Fragment>
          }
        />
        {formVisible && (
          <React.Fragment>
            <Divider />
            <Form onSubmit={() => this.handleAddToCart(item.slug)}>
              {data.variations.map(v => {
                const name = v.name.toLowerCase();
                return (
                  <Form.Field key={v.id}>
                    <Select
                      name={name}
                      onChange={this.handleChange}
                      placeholder={`Select a ${name}`}
                      fluid
                      selection
                      options={v.item_variations.map(item => {
                        return {
                          key: item.id,
                          text: item.value,
                          value: item.id
                        };
                      })}
                      value={formData[name]}
                    />
                  </Form.Field>
                );
              })}
              <Form.Button primary>Add</Form.Button>
            </Form>
          </React.Fragment>
        )}
      </Grid.Column>
      <Grid.Column>
        <Header as="h2">Try different variations</Header>
        {data.variations &&
          data.variations.map(v => {
            return (
              <React.Fragment key={v.id}>
                <Header as="h3">{v.name}</Header>
                <Item.Group divided>
                  {v.item_variations.map(iv => {
                    return (
                      <Item key={iv.id}>
                        {iv.attachment && (
                          <Item.Image
                            size="tiny"
                            src={`http://127.0.0.1:8000${iv.attachment}`}
                          />
                        )}
                        <Item.Content verticalAlign="middle">
                          {iv.value}
                        </Item.Content>
                      </Item>
                    );
                  })}
                </Item.Group>
              </React.Fragment>
            );
          })}
      </Grid.Column>
    </Grid.Row>
  </Grid>

)
}
}
 export default ProductDetail;
